<?php\ninclude('simple_html_dom.php');\n\nclass ArticleExtractor {\n    private $html;\n\n    public function __construct($url) {\n        $this->html = file_get_html($url);\n    }\n\n    public function extractArticles() {\n        $articles = [];\n        foreach($this->html->find('article') as $article) {\n            $articles[] = [\n                'title' => $this->extractTitle($article),\n                'image' => $this->extractImage($article),\n                'date' => $this->extractDate($article),\n                'description' => $this->extractDescription($article)\n            ];\n        }\n        return $articles;\n    }\n\n    private function extractTitle($article) {\n        return $article->find('h2', 0)->plaintext;\n    }\n\n    private function extractImage($article) {\n        return $article->find('img', 0)->src;\n    }\n\n    private function extractDate($article) {\n        return $article->find('.date', 0)->plaintext;\n    }\n\n    private function extractDescription($article) {\n        return $article->find('.description', 0)->plaintext;\n    }\n}\n\n$url = "http://example.com/articles";\n$extractor = new ArticleExtractor($url);\n$articles = $extractor->extractArticles();\n\nprint_r($articles);\n?>