<?php\nrequire_once('lib/nusoap.php');\n\n// Initialize the client with the WSDL URL\n$client = new nusoap_client('http://localhost:3333/Service.asmx?wsdl', 'wsdl');\n\n// Check for any construction errors\n$err = $client->getError();\nif ($err) {\n    echo "Constructor error: " . $err . "\n";\n    exit();\n}\n\n// Create the parameters array\n$params = array(\n    'anId' => 3,\n    'action' => 'OMNOMNOMNOM',\n    'parameters' => array(\n        'Param' => array(\n            array(\n                'Value' => 'value1',\n                'Name' => 'name1'\n            ),\n            array(\n                'Value' => 'value2',\n                'Name' => 'name2'\n            )\n        )\n    )\n);\n\n// Call the web service method\n$result = $client->call('DoSomething', $params, '', 'http://tempuri.org/webservices/DoSomething');\n\n// Check for a fault\nif ($client->fault) {\n    echo "Fault: ";\n    print_r($result);\n} else {\n    // Check for errors\n    $err = $client->getError();\n    if ($err) {\n        echo "Error: " . $err . "\n";\n    } else {\n        // Display the result\n        print_r($result);\n    }\n}\n?>