function checkPHPSyntax($file) {\n    // Ensure the file exists\n    if (!file_exists($file)) {\n        return "File not found.";\n    }\n\n    // Escape the file path to prevent command injection\n    $escapedFilePath = escapeshellarg($file);\n\n    // Execute the syntax check command\n    $output = shell_exec("php -l $escapedFilePath 2>&1");\n\n    // Check the output for syntax errors\n    if (strpos($output, 'No syntax errors detected') !== false) {\n        return "No syntax errors detected.";\n    }\n\n    return "Syntax errors detected: " . $output;\n}\n\n// Usage example\n$file = 'path/to/your/file.php';\n$result = checkPHPSyntax($file);\necho $result;