<?php\n// Database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to get the birthday from the database\n$leerling_id = intval($leerling_id); // Ensure $leerling_id is an integer to prevent SQL injection\n$query = "SELECT alg_persoonsgegevens_geboortedatum FROM alg_persoonsgegevens WHERE alg_persoonsgegevens_leerling_ID = $leerling_id";\n$result = $mysqli->query($query);\n\nif ($result->num_rows > 0) {\n    $row = $result->fetch_assoc();\n    $birthdate = $row['alg_persoonsgegevens_geboortedatum'];\n\n    // Create DateTime objects\n    $birthDate = new DateTime($birthdate);\n    $currentDate = new DateTime('today');\n\n    // Calculate the age\n    $age = $birthDate->diff($currentDate)->y;\n\n    echo "Age: " . $age;\n} else {\n    echo "No records found.";\n}\n\n// Close connection\n$mysqli->close();\n?>