<?php\n   require_once 'vendor/autoload.php';\n\n   // Configure HTML Purifier\n   $config = HTMLPurifier_Config::createDefault();\n   \n   // Allow only specific tags and attributes\n   $config->set('HTML.SafeEmbed', true);\n   $config->set('HTML.SafeObject', true);\n   $config->set('HTML.SafeIframe', true);\n   $config->set('URI.SafeIframeRegexp', '%^(http://|https://|//)(www.youtube.com/embed/|player.vimeo.com/video/)%;');\n   $config->set('Attr.AllowedFrameTargets', ['_blank', '_self', '_parent', '_top']);\n   $config->set('HTML.Allowed', 'embed[width|height|src|type|allowscriptaccess|allowfullscreen],object[width|height|data|type],param[name|value]');\n\n   $purifier = new HTMLPurifier($config);\n\n   if ($_SERVER['REQUEST_METHOD'] === 'POST') {\n       // Get user input\n       $userInput = $_POST['html_code'];\n\n       // Sanitize it\n       $cleanHtml = $purifier->purify($userInput);\n\n       // Output the sanitized HTML\n       echo $cleanHtml;\n   }\n   ?>\n\n   <form method="post">\n       <textarea name="html_code" rows="10" cols="50"></textarea>\n       <br>\n       <input type="submit" value="Submit">\n   </form>