// Autoload function\nspl_autoload_register(function ($class_name) {\n    include $class_name . '.php';\n});\n\nabstract class Auto\n{\n    // ... some stuff ...\n\n    public static function create($type)\n    {\n        switch ($type) {\n            case 'Truck':\n                return new Truck();\n                break;\n            case 'Car':\n            default:\n                return new Car();\n                break;\n        }\n    }\n}\n\n// Usage example\n$car = Auto::create('Car');\n$truck = Auto::create('Truck');