class Database {\n    private static $instance = null;\n    private $connection;\n\n    // Make the constructor private to prevent direct creation of object\n    private function __construct() {\n        // Initialize the database connection here\n        $this->connection = new PDO('mysql:host=hostname;dbname=database', 'username', 'password');\n    }\n\n    // Public method to get the instance of the Database class\n    public static function getInstance() {\n        if (self::$instance == null) {\n            self::$instance = new Database();\n        }\n        return self::$instance;\n    }\n\n    // Example function to get the connection\n    public function getConnection() {\n        return $this->connection;\n    }\n\n    // Prevent cloning of the instance\n    private function __clone() { }\n\n    // Prevent unserialization of the instance\n    private function __wakeup() { }\n}