<?php\n\n// Ensure the pcntl extension is loaded\nif (!function_exists('pcntl_exec')) {\n    die("pcntl extension is not available.\n");\n}\n\n// Command to run\n$command = 'mysql';\n$args = ['-u', 'root', '-pxxxx', 'db_name'];\n\n// Replace the current process with the MySQL shell\npcntl_exec($command, $args);\n\n// If pcntl_exec fails, an error message will be printed\ndie("Failed to execute $command\n");\n\n?>