class MyClass {\n    public function myMethod($arg1, $arg2, $arg3) {\n        // Method implementation\n        echo "Arguments: $arg1, $arg2, $arg3";\n    }\n\n    public function invokeMethod($methodName, ...$args) {\n        if (method_exists($this, $methodName)) {\n            $this->{$methodName}(...$args);\n        } else {\n            echo "Method $methodName does not exist.";\n        }\n    }\n}\n\n$instance = new MyClass();\n$instance->invokeMethod('myMethod', 'arg1', 'arg2', 'arg3');