<?php\nif (isset($_GET["page"]) && is_numeric($_GET["page"])) {\n    $page = intval($_GET["page"]);\n    $con = new mysqli("localhost", "username", "password", "jmurano_pages");\n\n    if ($con->connect_error) {\n        die("Connection failed: " . $con->connect_error);\n    }\n\n    // Prepare and execute the title query\n    $stmt = $con->prepare("SELECT title FROM pageContent WHERE pageID = ?");\n    $stmt->bind_param("i", $page);\n    $stmt->execute();\n    $stmt->bind_result($title);\n    $stmt->fetch();\n    $stmt->close();\n\n    echo "<title>" . htmlspecialchars($title) . "</title>\n";\n    echo "</head>\n";\n    echo "<body>\n";\n\n    // Prepare and execute the content query\n    $stmt = $con->prepare("SELECT content FROM pageContent WHERE pageID = ?");\n    $stmt->bind_param("i", $page);\n    $stmt->execute();\n    $stmt->bind_result($content);\n    $stmt->fetch();\n    $stmt->close();\n\n    echo htmlspecialchars($content);\n    echo "\n</body>\n</html>";\n\n    $con->close();\n} else {\n    echo "Invalid page parameter.";\n}\n?>