<?php\nheader('Content-Type: text/html; charset=utf-8');\n\n// Establish a new MySQLi connection\n$link = new mysqli('hostname', 'username', 'password', 'database');\nif ($link->connect_error) {\n    die('Connection failed: ' . $link->connect_error);\n}\n$link->set_charset("utf8");\n\nif ($_POST['action'] == 'i') {\n    $username = $link->real_escape_string($_POST['username']);\n    $note = $link->real_escape_string($_POST['note']);\n    \n    $stmt = $link->prepare("INSERT INTO app_notes (username, note, created_at) VALUES (?, ?, NOW())");\n    $stmt->bind_param('ss', $username, $note);\n    \n    if ($stmt->execute()) {\n        echo '1 - ' . $stmt->insert_id;\n    } else {\n        echo '0 - Error: ' . $stmt->error;\n    }\n    \n    $stmt->close();\n}\n$link->close();\n?>