// Assume $email is obtained from a user input form and sanitized.\n$email = filter_input(INPUT_POST, 'email', FILTER_SANITIZE_EMAIL);\n\nif (filter_var($email, FILTER_VALIDATE_EMAIL)) {\n    // Query the database to find the username associated with the email\n    $stmt = $pdo->prepare("SELECT username FROM users WHERE email = :email");\n    $stmt->bindParam(':email', $email);\n    $stmt->execute();\n    $user = $stmt->fetch();\n\n    if ($user) {\n        // Send the username to the user's email address\n        $to = $email;\n        $subject = "Your Username";\n        $message = "Hello, your username is: " . $user['username'];\n        $headers = "From: noreply@yourdomain.com";\n\n        mail($to, $subject, $message, $headers);\n    } else {\n        // Handle the case where the email is not found\n        echo "No account found with that email address.";\n    }\n} else {\n    echo "Invalid email address.";\n}