<?php\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\n// Prepare the statement\n$stmt = $mysqli->prepare("SELECT * FROM table1 WHERE id = ? AND name = ?");\n\n// Bind parameters\n$id = 20;\n$name = "John";\n$stmt->bind_param("is", $id, $name);\n\n// Manually construct the query for debugging\n$query = "SELECT * FROM table1 WHERE id = " . $mysqli->real_escape_string($id) . " AND name = '" . $mysqli->real_escape_string($name) . "'";\necho $query;\n\n// Execute the statement\n$stmt->execute();\n$result = $stmt->get_result();\n\n// Fetch the results\nwhile ($row = $result->fetch_assoc()) {\n    print_r($row);\n}\n\n$stmt->close();\n$mysqli->close();\n?>