<?php\nclass MyClass {\n    public function myMethod($param) {\n        echo "Method called with parameter: " . $param;\n    }\n}\n\n$object = new MyClass();\n$methodName = 'myMethod';\n$parameter = 'Hello, World!';\n\n// Using call_user_func\ncall_user_func([$object, $methodName], $parameter);\n\n// Using call_user_func_array\ncall_user_func_array([$object, $methodName], [$parameter]);\n?>