<?php\n// Check if file was uploaded\nif (isset($_FILES['uploaded_file'])) {\n    $uploadedFile = $_FILES['uploaded_file']['tmp_name'];\n\n    // Create a new Fileinfo resource\n    $finfo = finfo_open(FILEINFO_MIME_TYPE);\n\n    // Get the MIME type of the uploaded file\n    $fileType = finfo_file($finfo, $uploadedFile);\n\n    // Close the Fileinfo resource\n    finfo_close($finfo);\n\n    // Define allowed MIME types\n    $allowedTypes = ['image/jpeg', 'image/png', 'application/pdf', 'application/msword', 'application/vnd.ms-excel', 'text/plain'];\n\n    // Check if the file type is allowed\n    if (in_array($fileType, $allowedTypes)) {\n        echo "File is of a valid type: " . $fileType;\n        // Proceed with further processing (e.g., move the file to a permanent location)\n    } else {\n        echo "Invalid file type: " . $fileType;\n    }\n} else {\n    echo "No file uploaded.";\n}\n?>