<?php\n// Check if file was uploaded\nif (isset($_FILES['uploaded_file'])) {\n    $uploadedFile = $_FILES['uploaded_file']['tmp_name'];\n\n    // Get image size information\n    $imageInfo = getimagesize($uploadedFile);\n\n    if ($imageInfo !== false) {\n        $fileType = $imageInfo['mime'];\n        echo "File is a valid image type: " . $fileType;\n        // Proceed with further processing (e.g., move the file to a permanent location)\n    } else {\n        echo "Invalid image file.";\n    }\n} else {\n    echo "No file uploaded.";\n}\n?>