<?php\nnamespace Deployer;\n\nrequire 'recipe/common.php';\n\nset('repository', 'git@github.com:your/repository.git');\nset('shared_files', ['.env']);\nset('shared_dirs', ['storage']);\nset('writable_dirs', ['storage']);\nset('keep_releases', 5);\n\nhost('development')\n    ->hostname('dev.yoursite.com')\n    ->user('username')\n    ->set('deploy_path', '/var/www/dev');\n\nhost('staging')\n    ->hostname('staging.yoursite.com')\n    ->user('username')\n    ->set('deploy_path', '/var/www/staging');\n\nhost('production')\n    ->hostname('yoursite.com')\n    ->user('username')\n    ->set('deploy_path', '/var/www/html');\n\ntask('deploy', [\n    'deploy:prepare',\n    'deploy:lock',\n    'deploy:release',\n    'deploy:update_code',\n    'deploy:shared',\n    'deploy:vendors',\n    'deploy:writable',\n    'artisan:migrate',\n    'deploy:symlink',\n    'deploy:unlock',\n    'cleanup',\n]);\n\nafter('deploy:failed', 'deploy:unlock');