<?php\n// -----------------------------------------------------\n//it displays appropriate columns based on what table you are viewing\nfunction displayTable($table, $order, $sort) {\n    // Adjust the query to include a join for the volunteers_2009 table\n    if ($table == 'volunteers_2009') {\n        $query = "SELECT volunteers_2009.*, venues.venue_name \n                  FROM volunteers_2009 \n                  LEFT JOIN venues ON volunteers_2009.venue_id = venues.id \n                  ORDER BY $order $sort";\n    } else {\n        $query = "SELECT * FROM $table ORDER BY $order $sort";\n    }\n\n    $result = mysql_query($query);\n\n    if ($_POST) { ?>\n        <table>\n            <tr>\n                <th>Name</th>\n                <?php if ($table == 'maillist') { ?>\n                    <th>Email</th>\n                <?php } ?>\n                <?php if ($table == 'volunteers_2008' || $table == 'volunteers_2009') { ?>\n                    <th>Comments</th>\n                <?php } ?>\n                <?php if ($table == 'volunteers_2009') { ?>\n                    <th>Interests</th>\n                    <th>Venue</th>\n                <?php } ?>\n                <th>Edit</th>\n            </tr>\n            <?php while ($row = mysql_fetch_array($result)) { ?>\n                <tr>\n                    <td><?= htmlspecialchars($row['fname'] . ' ' . $row['lname']) ?></td>\n                    <?php if ($table == 'maillist') { ?>\n                        <td><?= htmlspecialchars(strtolower($row['email'])) ?></td>\n                    <?php } ?>\n                    <?php if ($table == 'volunteers_2008' || $table == 'volunteers_2009') { ?>\n                        <td><?= htmlspecialchars(substr($row['comments'], 0, 32)) ?></td>\n                    <?php } ?>\n                    <?php if ($table == 'volunteers_2009') { ?>\n                        <td><?= '1) ' . htmlspecialchars($row['choice1']) . ' 2) ' . htmlspecialchars($row['choice2']) . ' 3) ' . htmlspecialchars($row['choice3']) ?></td>\n                        <td><?= htmlspecialchars($row['venue_name']) ? htmlspecialchars($row['venue_name']) : 'No Venue Assigned' ?></td>\n                    <?php } ?>\n                    <td>\n                        <a href="edit.php?id=<?= $row['id'] ?>&table=<?= $table ?>">Upd</a> / \n                        <a href="delete.php?id=<?= $row['id'] ?>&table=<?= $table ?>" onclick="return confirm('Are you sure you want to delete?')">Del</a>\n                    </td>\n                </tr>\n            <?php } ?>\n        </table>\n    <?php }\n}\n// -----------------------------------------------------\n?>