<?php\n\n// Sample data\n$data = "Ryu Aiter D78:21:87:13 177 /177 1 / 6\nRyu Chronos D78:21:26:21 182 /182 0 / 6\nRyu Hermes D78:21:26:22 201 /201 0 / 6\nRyu Hefaistos D78:31:75:10 136 /136 1 / 2\nRyu Krotos D78:84:96:11 170 /170 1 / 6\nRyu Heros D78:65:51:31 175 /175 2 / 5\nRyu Arachnos D78:13:84:11 185 /185 0 / 5";\n\n// Split data into rows\n$rows = explode("\n", $data);\n\nforeach ($rows as $row) {\n    // Split row into parts\n    if (preg_match('/(.{1,16}) (D.{11}) (\d+) \/(\d+) (\d+) \/ (\d+)/', $row, $matches)) {\n        $base = trim($matches[1]);\n        $location = $matches[2];\n        $econ = (int)$matches[3];\n        $max_econ = (int)$matches[4];\n        $used = (int)$matches[5];\n        $total = (int)$matches[6];\n\n        // Output the variables (for debugging/testing purposes)\n        echo "Base: $base\n";\n        echo "Location: $location\n";\n        echo "Econ: $econ\n";\n        echo "Max Econ: $max_econ\n";\n        echo "Used: $used\n";\n        echo "Total: $total\n\n";\n    }\n}\n?>