// Setting a session ID cookie in PHP\nsession_start();\n$_SESSION['userPreferences'] = [\n    'theme' => 'dark',\n    'fontSize' => '14px',\n    'language' => 'en'\n];\nsetcookie('session_id', session_id(), time() + (86400 * 30), "/"); // 30 days expiration\n\n// Retrieving session data in PHP\nsession_start();\n$userPreferences = $_SESSION['userPreferences'] ?? [];\nprint_r($userPreferences);