<?php\n// Database connection\n$servername = "your_server_name";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database_name";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Fetch all names from the 'people' table\n$sql = "SELECT name FROM people";\n$result = $conn->query($sql);\n\n// Store names in an array\n$namesInDatabase = [];\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        $namesInDatabase[] = $row['name'];\n    }\n}\n\n$conn->close();\n\n// Your array of strings\n$foo = ["John", "Jane", "Doe", "Alice", "Bob"];\n\n// Find strings in $foo that are not in the 'people' table\n$notInDatabase = array_diff($foo, $namesInDatabase);\n\n// Output the result\nprint_r($notInDatabase);\n?>