<?php\nfunction adjust_country_name($country) {\n    $keywords = array("bahamas","island","kingdom","republic","maldives","netherlands", "isle of man","ivory","philippines","seychelles","usa");\n    $exceptions = array("eire","hispaniola");\n\n    // Check if the country should have 'THE' added\n    foreach ($keywords as $keyword) {\n        if (stripos($country, $keyword) !== false) {\n            return 'THE ' . $country;\n        }\n    }\n\n    // Check if 'THE' should be removed\n    foreach ($exceptions as $exception) {\n        if (stripos($country, $exception) !== false) {\n            return str_ireplace('THE ', '', $country);\n        }\n    }\n\n    // If no changes needed\n    return $country;\n}\n\n// Example usage:\necho adjust_country_name("bahamas"); // Output: THE bahamas\necho adjust_country_name("eire"); // Output: eire\n?>