<?php\n$string = "This is a string that has exactly seventy-five characters in total length.";\n$length = strlen($string);\n$chars_per_line = ceil($length / 3);\n\n$line1 = substr($string, 0, $chars_per_line);\n$line2 = substr($string, $chars_per_line, $chars_per_line);\n$line3 = substr($string, 2 * $chars_per_line);\n\necho "<p>" . $line1 . "</p>";\necho "<p>" . $line2 . "</p>";\necho "<p>" . $line3 . "</p>";\n?>