class AuthController extends Zend_Controller_Action\n{\n    public function loginAction()\n    {\n        if ($this->getRequest()->isPost()) {\n            // Perform authentication\n            $authAdapter = new Zend_Auth_Adapter_DbTable(\n                Zend_Db_Table::getDefaultAdapter(),\n                'users',\n                'username',\n                'password'\n            );\n            $authAdapter->setIdentity($this->_request->getPost('username'))\n                        ->setCredential($this->_request->getPost('password'));\n\n            $auth = Zend_Auth::getInstance();\n            $result = $auth->authenticate($authAdapter);\n\n            if ($result->isValid()) {\n                // Authentication successful\n                $session = new Zend_Session_Namespace('auth_redirect');\n                if (isset($session->requestedUri)) {\n                    $redirectUri = $session->requestedUri;\n                    unset($session->requestedUri);\n                } else {\n                    $redirectUri = '/';\n                }\n\n                $this->_redirect($redirectUri);\n            } else {\n                // Authentication failed\n                $this->view->errorMessage = 'Invalid credentials provided';\n            }\n        }\n    }\n\n    public function logoutAction()\n    {\n        Zend_Auth::getInstance()->clearIdentity();\n        $this->_redirect('/');\n    }\n}