class Foo1 {\n    public function method1() {\n        echo "Method from Foo1\n";\n    }\n}\n\nclass Foo2 {\n    private $foo1;\n\n    public function __construct() {\n        $this->foo1 = new Foo1();\n    }\n\n    public function method2() {\n        echo "Method from Foo2\n";\n        $this->foo1->method1(); // Call method from Foo1\n    }\n}\n\n$instance = new Foo2();\n$instance->method2();