<?php\n\n// Function to autoload classes\nspl_autoload_register(function ($class_name) {\n    // Check if the class starts with 'vPrimaryKey_'\n    if (strpos($class_name, 'vPrimaryKey_') === 0) {\n        $table = substr($class_name, strlen('vPrimaryKey_'));\n        \n        // Define the class dynamically\n        eval("\n            class $class_name extends vPrimaryKey {\n                public function __construct() {\n                    parent::__construct('$table');\n                }\n            }\n        ");\n    }\n});\n\n// Base class for primary key validation\nclass vPrimaryKey {\n    protected $table;\n\n    public function __construct($table) {\n        $this->table = $table;\n    }\n\n    public function validate($id) {\n        // Validation logic here\n        echo 'Validating ' . $id . ' in table ' . $this->table . "\n";\n    }\n}\n\n// Example usage\n$clsName = 'vPrimaryKey_Products';\n$cls = new $clsName();\n$cls->validate(123);\n\n?>