public function your_method($id)\n{\n    // Load your model\n    $this->load->model('your_model');\n\n    // Query the database\n    $data = $this->your_model->get_data_by_id($id);\n\n    // Check if the data exists\n    if (empty($data)) {\n        // If no data is found, show a 404 error\n        show_404();\n    }\n\n    // If data is found, proceed with your normal logic\n    $this->load->view('your_view', $data);\n}