<?php\nrequire 'vendor/autoload.php';\n\nuse PhpOffice\PhpWord\IOFactory;\nuse PhpOffice\PhpWord\Writer\HTML;\n\n// Load the Word document\n$phpWord = IOFactory::load('path/to/your/document.docx');\n\n// Save it as HTML\n$htmlWriter = new HTML($phpWord);\n$htmlFilePath = 'path/to/your/output.html';\n$htmlWriter->save($htmlFilePath);\n\n// Read the HTML content\n$htmlContent = file_get_contents($htmlFilePath);\n\n// Optionally, you can further process $htmlContent to convert it to XHTML\n// For the purpose of this example, we'll just output it\necho $htmlContent;