$pattern = "/^[A-Z][a-zA-Z]*(?:[ '-][a-zA-Z]+)*(?: & [A-Z][a-zA-Z]*)?$/";\n\n$names = ["Worthington-Smythe", "D'Angelo", "Van der Humpton", "Ben & Jerry", "Invalid-Name-", "Another Invalid Name&"];\n\nforeach ($names as $name) {\n    if (preg_match($pattern, $name)) {\n        echo "$name is a valid name.\n";\n    } else {\n        echo "$name is not a valid name.\n";\n    }\n}\n?>