<?php\n   $h2 = 'IPADDRESS';\n   $dbu = 'DBUSER';\n   $dbp = 'DBPASS';\n   $dbname = 'your_database';\n\n   // Create connection\n   $DBlink = new mysqli($h2, $dbu, $dbp, $dbname);\n\n   // Check connection\n   if ($DBlink->connect_error) {\n       die("Connection failed: " . $DBlink->connect_error);\n   }\n   echo "Connected successfully";\n\n   // Close connection\n   $DBlink->close();\n   ?>