<?php\n// Ensure the session is started\nsession_start();\n\n// Define the directory and ensure it exists\n$dirToCreate = ".." . $_SESSION['s_USER_URL'];\nif (!is_dir($dirToCreate)) {\n    mkdir($dirToCreate, 0777, true);\n}\n\n// Define the file path\n$ourFileName = $dirToCreate . "/index.php";\n\n// Check if the file already exists\nif (!file_exists($ourFileName)) {\n    // Create and write to the file\n    $stringData = "<?php echo 'Hi'; ?>";\n    \n    // fopen with 'w' mode to create and write\n    if (file_put_contents($ourFileName, $stringData) === false) {\n        die("Can't write to file");\n    } else {\n        echo "File created and data written successfully.";\n    }\n} else {\n    echo "File already exists.";\n}\n?>