function modifyArrayValues(&$array, $searchPattern, $insertString) {\n    array_walk_recursive($array, function (&$value) use ($searchPattern, $insertString) {\n        if (strpos($value, $searchPattern) !== false) {\n            $value = str_replace($searchPattern, $searchPattern . $insertString, $value);\n        }\n    });\n}\n\n$data = [\n    "first" => ["baz1"],\n    "second" => ["foo1"],\n    "third" => ["foo2"]\n];\n\n$searchPattern = "foo";\n$insertString = "-bar";\n\nmodifyArrayValues($data, $searchPattern, $insertString);\n\nprint_r($data);