<?php\n// Connection to the first database\n$hostname1 = 'hostname1';\n$username1 = 'username1';\n$password1 = 'password1';\n$database1 = 'database1';\n\n$conn1 = new mysqli($hostname1, $username1, $password1, $database1);\n\nif ($conn1->connect_error) {\n    die("Connection failed: " . $conn1->connect_error);\n}\n\n// Connection to the second database\n$hostname2 = 'hostname2';\n$username2 = 'username2';\n$password2 = 'password2';\n$database2 = 'database2';\n\n$conn2 = new mysqli($hostname2, $username2, $password2, $database2);\n\nif ($conn2->connect_error) {\n    die("Connection failed: " . $conn2->connect_error);\n}\n\n// Example query on the first database\n$sql1 = "SELECT * FROM table_in_database1";\n$result1 = $conn1->query($sql1);\n\nif ($result1->num_rows > 0) {\n    while($row1 = $result1->fetch_assoc()) {\n        echo "Data from database1: " . $row1["column_name"] . "<br>";\n    }\n} else {\n    echo "0 results from database1";\n}\n\n// Example query on the second database\n$sql2 = "SELECT * FROM table_in_database2";\n$result2 = $conn2->query($sql2);\n\nif ($result2->num_rows > 0) {\n    while($row2 = $result2->fetch_assoc()) {\n        echo "Data from database2: " . $row2["column_name"] . "<br>";\n    }\n} else {\n    echo "0 results from database2";\n}\n\n// Close the connections\n$conn1->close();\n$conn2->close();\n?>