function add_custom_class_to_home_menu_item($classes, $item, $args) {\n    // Check if the current page is the static front page\n    if (is_front_page() && $item->object_id == get_option('page_on_front')) {\n        // Add the 'current_page_item' class\n        $classes[] = 'current_page_item';\n    }\n    return $classes;\n}\nadd_filter('nav_menu_css_class', 'add_custom_class_to_home_menu_item', 10, 3);