<?php\nif (isset($_POST["id_tuto"])) {\n    PrintHtml($_POST["id_tuto"]);\n}\n\nfunction PrintHtml($id) {\n    $filePath = getHtmlFilePath($id);\n    if ($filePath && file_exists($filePath)) {\n        echo file_get_contents($filePath);\n    } else {\n        echo "Content not found.";\n    }\n}\n\nfunction getHtmlFilePath($id) {\n    // Map tutorial IDs to file paths\n    $fileMap = [\n        "tutorial1" => "tutorials/tutorial1.html",\n        "tutorial2" => "tutorials/tutorial2.html",\n        // Add more mappings as needed\n    ];\n    \n    return isset($fileMap[$id]) ? $fileMap[$id] : null;\n}\n?>