<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Retrieve the data from the textarea\n    $datafeed = $_POST['datafeed'];\n    \n    // Split the input into lines\n    $lines = explode("\n", $datafeed);\n    \n    foreach ($lines as $line) {\n        // Trim any whitespace from the line\n        $line = trim($line);\n\n        // Skip empty lines\n        if (empty($line)) {\n            continue;\n        }\n\n        // Split the line by the delimiter "|"\n        $fields = explode("|", $line);\n\n        // Process each field as needed (example: print each field)\n        foreach ($fields as $field) {\n            // Trim any whitespace from the field\n            $field = trim($field);\n            \n            echo htmlspecialchars($field) . "<br>";\n        }\n    }\n}\n?>