<?php\n// Database connection\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to get distinct year and month with posts\n$query = "SELECT \n            DATE_FORMAT(FROM_UNIXTIME(date), '%Y-%m') AS year_month,\n            DATE_FORMAT(FROM_UNIXTIME(date), '%M %Y') AS formatted_date\n          FROM posts\n          GROUP BY year_month\n          ORDER BY year_month DESC";\n\n$result = $mysqli->query($query);\n\nif ($result->num_rows > 0) {\n    echo "<ul>\n";\n    while ($row = $result->fetch_assoc()) {\n        echo "<li>" . $row['formatted_date'] . "</li>\n";\n    }\n    echo "</ul>\n";\n} else {\n    echo "No posts available.";\n}\n\n// Close the connection\n$mysqli->close();\n?>