// -- myFile.txt --\n// Mary had a little $pet.\n\n// -- parser.php --\n$pet = "lamb";\n\n// Open and read the contents of the file\n$fileContents = file_get_contents('myFile.txt');\n\n// Function to replace variables in the text\nfunction substituteVariables($text, $variables) {\n    return preg_replace_callback('/\$(\w+)/', function($matches) use ($variables) {\n        $varName = $matches[1];\n        return isset($variables[$varName]) ? $variables[$varName] : $matches[0];\n    }, $text);\n}\n\n// Get all defined variables\n$allVars = get_defined_vars();\n\n// Substitute variables in the file contents\n$newContents = substituteVariables($fileContents, $allVars);\n\necho $newContents; // Outputs: "Mary had a little lamb."