// Securely fetch user data from the database\n$user = mysqli_real_escape_string($conn, $_POST['user']);\n$sql = "SELECT * FROM table WHERE user='$user'";\n$result = mysqli_query($conn, $sql);\n$myrow = mysqli_fetch_assoc($result);\n\n// Use password_hash for storing passwords and password_verify for checking passwords\n$somepass = $_POST['password'];\n$storedPass = $myrow['password']; // Assuming the password is stored in the 'password' field\n\n// Verify the password\nif (!password_verify($somepass, $storedPass)) {\n    echo 'error';\n} else {\n    echo 'welcome';\n}\n?>