<?php\n// Database connection\n$conn = new mysqli('localhost', 'username', 'password', 'database');\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Query to fetch data\n$sql = "SELECT org.name AS org_name, emp.name AS emp_name, emp.phone AS emp_phone, emp.address AS emp_address \n        FROM organisations org\n        INNER JOIN employees emp ON org.id = emp.org_id\n        ORDER BY org.name, emp.name";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    $data = [];\n    while($row = $result->fetch_assoc()) {\n        $data[$row['org_name']][] = $row;\n    }\n\n    echo '<table border="1">';\n    foreach ($data as $org_name => $employees) {\n        $rowspan = count($employees);\n        $first = true;\n        foreach ($employees as $employee) {\n            echo '<tr>';\n            if ($first) {\n                echo '<td rowspan="' . $rowspan . '">' . htmlspecialchars($org_name) . '</td>';\n                $first = false;\n            }\n            echo '<td>' . htmlspecialchars($employee['emp_name']) . ', ' . htmlspecialchars($employee['emp_phone']) . ', ' . htmlspecialchars($employee['emp_address']) . '</td>';\n            echo '</tr>';\n        }\n    }\n    echo '</table>';\n} else {\n    echo "No records found.";\n}\n\n$conn->close();\n?>