function url_exists($url) {\n    // Use get_headers() to get the HTTP response headers\n    $headers = @get_headers($url);\n\n    // Check if headers were retrieved successfully\n    if ($headers && strpos($headers[0], '200') !== false) {\n        return true; // URL points to a valid file\n    } else {\n        return false; // URL does not point to a valid file\n    }\n}\n\n// Example usage\n$url = "http://example.com/file.js";\nif (url_exists($url)) {\n    echo "The URL points to a valid file.";\n} else {\n    echo "The URL does not point to a valid file.";\n}