$mysqli = new mysqli("hostname", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("SELECT *, DATE_FORMAT(timestamp, '%W %D %M %Y') as formatted_date FROM articleDB WHERE userID = ? ORDER BY timestamp DESC LIMIT 8");\n$stmt->bind_param("s", $_SESSION["**"]);\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    echo 'Posted: ' . $row['formatted_date'] . '<br>';\n}\n\n$stmt->close();\n$mysqli->close();