<?php\n// Prepare your SQL select statement\n$sql = "SELECT datetime_column FROM your_table WHERE id = ?";\n\n// Use prepared statements to avoid SQL injection\n$stmt = $pdo->prepare($sql);\n$stmt->execute([$id]);\n$row = $stmt->fetch(PDO::FETCH_ASSOC);\n\nif ($row) {\n    // Convert the datetime value to UNIX timestamp\n    $datetime = $row['datetime_column'];\n    $timestamp = strtotime($datetime);\n    echo $timestamp;\n}\n?>