// Create connection\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare and bind\n$stmt = $mysqli->prepare("INSERT INTO table_name (column_name) VALUES (?)");\n$stmt->bind_param("s", $input_string);\n\n// Set parameters and execute\n$input_string = "Your input string with 'single quotes' and \"double quotes\"";\n$stmt->execute();\n\n// Retrieve the data\n$result = $mysqli->query("SELECT column_name FROM table_name WHERE condition");\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        $retrieved_string = $row['column_name'];\n        echo $retrieved_string;\n    }\n}\n\n// Close connection\n$stmt->close();\n$mysqli->close();