function getRawSql($query, $params) {\n    foreach ($params as $key => $value) {\n        $query = preg_replace('/:' . $key . '\b/', '\'' . addslashes($value) . '\'', $query);\n    }\n    return $query;\n}\n\n// Example usage\n$sql = "SELECT * FROM users WHERE id = :id AND username = :username";\n$params = [ 'id' => 1, 'username' => 'exampleuser' ];\n\n$rawSql = getRawSql($sql, $params);\necho $rawSql;