<?php\n// Your list of IDs\n$var = "1 3 5 7 9";\n\n// Convert space-separated string to an array\n$idsArray = explode(" ", $var);\n\n// Database connection (replace with your connection details)\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check the connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Prepare placeholders for the query\n$placeholders = implode(',', array_fill(0, count($idsArray), '?'));\n\n// Prepare the SQL statement\n$sql = "UPDATE tablename SET seen_before = 1 WHERE id IN ($placeholders)";\n$stmt = $conn->prepare($sql);\n\n// Bind the parameters\n$types = str_repeat('i', count($idsArray)); // Assuming all IDs are integers\n$stmt->bind_param($types, ...$idsArray);\n\n// Execute the query\nif ($stmt->execute()) {\n    echo "Records updated successfully";\n} else {\n    echo "Error updating records: " . $stmt->error;\n}\n\n// Close the connection\n$stmt->close();\n$conn->close();\n?>