<?php\n$args = array(\n    'post_type' => 'page',\n    'post_parent' => $parent_id, // Replace $parent_id with the ID of the parent page\n    'orderby' => 'title',\n    'order' => 'ASC'\n);\n\n$query = new WP_Query($args);\n\nif ($query->have_posts()) :\n    while ($query->have_posts()) : $query->the_post(); ?>\n        <h2><?php the_title(); ?></h2>\n        <a href="<?php the_permalink(); ?>"><?php the_permalink(); ?></a>\n        <div><?php the_excerpt(); ?></div>\n    <?php endwhile;\n    wp_reset_postdata();\nelse :\n    echo '<p>No pages found.</p>';\nendif;\n?>