// Define the encryption key\n$encryption_key = 'YOUR_SECRET_KEY_HERE'; // Ensure this key is stored securely\n\n// Function to encrypt SSN\nfunction encrypt_ssn($ssn, $encryption_key) {\n    $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length('aes-256-cbc'));\n    $encrypted_ssn = openssl_encrypt($ssn, 'aes-256-cbc', $encryption_key, 0, $iv);\n    return base64_encode($encrypted_ssn . '::' . $iv);\n}\n\n// Function to decrypt SSN\nfunction decrypt_ssn($encrypted_ssn, $encryption_key) {\n    list($encrypted_data, $iv) = explode('::', base64_decode($encrypted_ssn), 2);\n    return openssl_decrypt($encrypted_data, 'aes-256-cbc', $encryption_key, 0, $iv);\n}\n\n// Example usage\n$ssn = '123-45-6789';\n$encrypted_ssn = encrypt_ssn($ssn, $encryption_key);\necho "Encrypted SSN: " . $encrypted_ssn . "\n";\n\n$decrypted_ssn = decrypt_ssn($encrypted_ssn, $encryption_key);\necho "Decrypted SSN: " . $decrypted_ssn . "\n";