<?php\nclass AlertManager {\n    private $alert = null;\n\n    public function validate() {\n        (!empty($_POST["From"]) && !empty($_POST["Body"])) ? $this->sendEmail() : $this->fuss();\n    }\n\n    private function sendEmail() {\n        $this->alert = "Lorem Ipsum";\n        mail("", "", $_POST["Body"], "From:" . $_POST["From"]);\n    }\n\n    private function fuss() {\n        $this->alert = "Dolor Sit";\n    }\n\n    public function showAlert() {\n        if (!is_null($this->alert)) echo $this->alert;\n    }\n}\n\nif (isset($_POST["Return"])) {\n    $alertManager = new AlertManager();\n    $alertManager->validate();\n    $alertManager->showAlert();\n}\n?>