<?php\n$Alert = null; // Initialize the global variable\n\nif (isset($_POST["Return"])) {\n    Validate();\n}\n\nfunction Validate() {\n    (!empty($_POST["From"]) && !empty($_POST["Body"])) ? Send_Email() : Fuss();\n}\n\nfunction Send_Email() {\n    global $Alert;\n    $Alert = "Lorem Ipsum";\n    mail("", "", $_POST["Body"], "From:" . $_POST["From"]);\n}\n\nfunction Fuss() {\n    global $Alert;\n    $Alert = "Dolor Sit";\n}\n\nfunction Alert() {\n    global $Alert;\n    if (!is_null($Alert)) echo $Alert;\n}\n?>